        [Fact]
        public void BeforeNmTest()
        {
            var privateKey1 = ByteUtil.HexToByteArray("CEF276FC1EDECF2029FF6DC491147D560521165EC832169745FCFAC15BB31693");
            var publicKey1 = ByteUtil.HexToByteArray("76DE6167395F475C3EAF3792070C967F4D47253E98E0C88EBEE7EF8551F4F272");
            var privateKey2 = ByteUtil.HexToByteArray("0A25B750C6E209FD8B0348D7354C7E6349B9728120F2AAB69358663138BFE59E");
            var publicKey2 = ByteUtil.HexToByteArray("DECD0337FA72F47A6C828679ABBBCBE6680457A8A71E5DBEF7C2D963F7E82342");
            var expSharedKey = ByteUtil.HexToByteArray("BD322B855633E411DC65BC89B10C63A006ED86F472CB46FFD7E72CA84EFDDEB8");

            byte[] shared = new byte[32];
            new BoxCurve25519XSalsa20Poly1305().BeforeNm(shared, 0, publicKey1, 0, privateKey2, 0);
            Assert.Equal(shared, expSharedKey);
            new BoxCurve25519XSalsa20Poly1305().BeforeNm(shared, 0, publicKey2, 0, privateKey1, 0);
            Assert.Equal(shared, expSharedKey);
        }

