            var expEnc = ByteUtil.HexToByteArray(expStr);

            var enc = new byte[raw.Length + 16];
            new BoxCurve25519XSalsa20Poly1305().EncryptSymmetric(enc, 0, key, 0, nonce, 0, raw, 0, raw.Length);
            Assert.Equal(expEnc, enc);

            var dec = new BoxCurve25519XSalsa20Poly1305().DecryptSymmetric(key, nonce, enc);
            Assert.Equal(dec, raw);
        }
    }
}
